import {useState,useEffect} from "react";
import axios from "axios";


function App() {
  //State
  const [notes,setNotes]=useState(null);
  const [createForm,setCreateForm]=useState(
    {
      title:"",
      body:"",
    }
  );
  const [updateForm,setUpdateForm]=useState({
    _id:null,
    title:"",
    body:""
  });

  useEffect(()=>{
    fetchNotes();
  },[]);



  const fetchNotes= async () => {
    //Fetch the Notes
    //Set to state
    const res= await axios.get("http://localhost:3000/notes");
    setNotes(res.data.note)
    console.log(res);


  };  

  const updateCreateFormField = (e)=>{
    const {name,value}=e.target;
    setCreateForm({
      ...createForm,
      [name]:value,
    })
  };

  const createNote = async (e) => {
e.preventDefault();
//Create the note
const res = await axios.post("http://localhost:3000/notes",createForm);

//Update state
setNotes([...notes,res.data.notes]);

//Clear form state
setCreateForm({
  title:"",
  body:"",
});

  };

  const deleteNote=async(_id)=>{
    //Delete the note
    const res=await axios.delete(`http://localhost:3000/notes/${_id}`);
    console.log(res);
    //Update State
    const newNotes=[...notes.filter(note=>{
      return note._id !== _id;
    })]
    setNotes(newNotes);
  };

  const handleUpdateFieldChange=(e)=>{
    const {value,name}=e.target

    setUpdateForm({
      ...updateForm,
      [name]:value,
    });
  };

  const toggleUpdate=(note)=>{

    //Set state on update form
    setUpdateForm({title:note.title,body:note.body})
  }



    return (
    <div className="App">
    <div>
      <h2>Notes:</h2>
      {notes && notes.map((note) => {
        return ( 
        <div key={note._id}>
          <h3>{note.title}</h3>
          <button onClick={()=>deleteNote(note._id)} >Delete note</button>
          <button onClick={()=> toggleUpdate(note)}>Update note</button>
        </div>

        );
      })

      }
    </div>

    <div>
      <h2>Update Note</h2>
      <form onChange={handleUpdateFieldChange}>
        <input value={updateForm.title} name="title"/>
        <textarea value={updateForm.body} name="body"/>
        <button type="submit">Update note</button>
      </form>
    </div>

    <div>
      <h2>Create note</h2>
      <form onSubmit={createNote}>
        <input onChange={updateCreateFormField} 
        value={createForm.title} 
        name="title"/>
        <textarea onChange={updateCreateFormField}  
        value={createForm.body} 
        name="body"/>
        <button type="submit">Create note</button>
      </form>


    </div>

    </div>


  );


}

export default App;
