import axios from 'axios';

const notesStore = create((set) => ({
  notes: null,
  fetchNotes : async ()=>{
    //Fetch the notes
    const res = await axios.get("http://localhost:3000/notes");
    //Set the state
    set({notes:res.data.note})
  },
  updateCreateFormField: ()=>{
